import {CRAFT_SLIDER, COMP_SLIDER, WINE_CHECK, BEER_CHECK, SPIRIT_CHECK} from './types';

export const craftSlider = (value) => dispatch =>{
    dispatch({    
            type:'CRAFT_SLIDER',
                payload: value
        }
    )
}
export const compSlider = (value) => dispatch => {
    return({
        type: 'COMP_SLIDER',
        payload: value
    });
}

export function wineCheck(value){
    return({
        type: 'WINE_CHECK',
        payload: value
    });
}

export function beerCheck(value){
    return({
        type: 'BEER_CHECK',
        payload: value
    });
}

export function spiritCheck(value){
    return({
        type: 'SPIRIT_CHECK',
        payload: value
    });
}
