import React from 'react';
import ReactDOM from 'react-dom';

import './App.css';
function submission(props){
          var barName = this.getElementById("barName").value;
        } 


class Slider extends React.Component{


  render(){
    let scale = [];
    let slider = [];
    let currentScale = [];
    let minThumb = null;
    let maxThumb = null;
    for(let i =0; i<=this.state.slots;i++){
          let label = "";
          if(i == 0 || i == 5 || i == 10){
            label = i;
          }
          scale.push(
            <div 
            key={i} 
            className="slot-scale">
              {label}
              </div>
            );
            let currentLabel = "";
          if(i === this.state.start || i === this.state.end){
            currentLabel = i;
          }
            currentScale.push(<div 
              key={i} 
            className="slot-scale">
              {label}
              </div>
            );
            if(i === this.state.end){
              maxThumb = <this.MaxSlider />
            } else{
              maxThumb = null;
            }
            let lineClass = "line";

            if(i>= this.state.start && i<this.state.end){
              lineClass += " line-selected";
            }
            slider.push(
                <div
                data-slot={i}
                onDragOver={this.onDragOver}
                onTouchMove = {this.onDragOver}
                onTouchEnd = {this.onDrop}
                onDrop = {this.onDrop}
                key={i}
                className="slot">
                <div data-slot={i} className={lineClass}/>
                <span className="scale-mark"></span>
                {maxThumb}
                </div>
              );
          }
    return(

      <div>
        <h2> React Slider</h2>
        <div className="topSlider">
         <div className = "slider-container">
           <div className = "slider-scale">
              {scale}
           </div>
           <div className="slider">
              {slider}
            </div>
            <div className="slider-selected-scale">
             {currentScale}
            </div>
          </div>
        </div>
      </div>
    );
  }
}
class App extends React.Component {
  constructor(props){
    super(props);
  }
  state = {
    slots: 10,
    start:0,
    end: 10,
    labelMode: "mid",
  }
  onDragOver = (e) =>{ e.preventDefault();}
  onDragStart = (e) =>{
    let slider = e.target.dataset.slider; 
    this.sliderType = slider;
  }
  onDrop = (e,target) =>{
    let source = this.sliderType;
    let slot = Number(e.target.dataset.slot);
    console.log("slot: ", slot);
      if(isNaN(slot)) return;
      if(source==="max"){
        if(slot>this.state.end) return;
        this.setState({
          end:slot
        },()=>{
          console.log(this.state);
        })
      }
      this.sliderType = null;
  }

  MaxSlider=()=>{
  return(
    <div data-slider = "max"
    onDragStart={this.onDragStart}
    onTouchStart={this.onDragStart}
    draggable className = "slider-thumb slider-thumb-max"></div>
    );
}
  render(){
  return (
    <div className="App">
      <header>
        <form>
        <label>
          What is the Barname: <br/>
          <input type = "text" id="barName"/>
        </label>
        </form>
       </header>
    </div>
  );
  }
}

export default App;
