//importing all relevant files to this file.
import React,{Component} from 'react';
import ReactDOM from 'react-dom';
import Slider from './Slider'
import Checkbox from './checkBox';
import axios from 'axios';

//importing css to this file
import './App.css';

// creating a Constant for the options that the users can click on
const OPTIONS = ["Wine", "Beer", "Spirits"];




//overall App class which takes all
class App extends React.Component {
  //constructor for the App class in which a super with the returned values passed through
  constructor(props){
    super(props);
    this.handleSubmit = this.handleSubmit.bind(this);
    //sets the checkbox values to false so it can be presented so to people.
    this.state={
    	ProductData: [],
      checkboxes: OPTIONS.reduce(
          (options, option) => ({
            ...options,
            [option]:false
          }),
          {}
        ),
      wineCheck: false,
      beerCheck: false,
      spiritCheck: false,
    };
  }
  //Submission for later use in order to return to API and place into database through Python
	 submission(event){
          var barName = this.getElementById("barName").value;
          var craftSlide = this.getElementById("craftBeerSlider").value;
          var wineSlide = this.getElementById("wineListSlider").value;
          var wineChecker = this.getElementById("wineCheck").value;
          var beerChecker = this.getElementById("beerCheck").value;
          var spiritCheck = this.getElementById("spiritCheck").value;
          var file = this.getElementById("dataFile").value;
          console.log("getting here");
         axios.get('https://localhost:5001/api/addBar/{barName}')
         		.then((response) =>{
         			console.log((response));
         			this.setState({
         				ProductData: response.data
         			});
         		});
         };

  // is a super function in order to select all values either selecting or de-selecting


  // allows for a passing through in order to select all checkboxes as true  

  // allows for the changing between true and false for individual checkboxes
  handleCheckboxChange = changeEvent => {
    //ginds the specific checkbox to be checked off
    const { name } = changeEvent.target;
    // changes the state dependant on whether it was previously true or false
    this.setState(prevState => ({
      checkboxes: {
        ...prevState.checkboxes,
        [name]: !prevState.checkboxes[name]
      }
    }));
  };
    //allows for event and changing of the checkbox
   handleSubmit(event){
   	event.preventDefault();
   	axios.get('https://localhost:5001/api/addBar/{barName}')
    .then((response) =>{
         			console.log((response));
         			this.setState({
         				ProductData: response.data
         			});
         		});
  };

  // allows for the creation within this class whilst using the imported files
  createCheckbox = option => (
    <Checkbox
      label={option}
      isSelected={this.state.checkboxes[option]}
      onCheckboxChange={this.handleCheckboxChange}
      key={option}
    />
  );
  // creates all the checkboxes based on the constants that are the options
  createCheckboxes = () => OPTIONS.map(this.createCheckbox);

  //calls the render function
  render(){
    //this is the return function that returns the html that helps create the app
    return (
      //a div class in order to have the specific section in question
      <div className="App">
        <header>
         <form onSubmit = {this.submit}>
           <label>
             <h2>What is the Barname: <br/></h2>
              <input type = "text" id="barName"/>
            </label>
            <h2> Craft Beer </h2>
            <Slider id="craftBeerSlider"/>
            <h2> Wine List Complexity</h2>
            <Slider id="wineListSlider"/>
            <h2>Beer Or Wine </h2>
            <Slider id="beerOrWine" />
            <h2>Wine, Beer Or Spirits </h2>
            <div className = "checkboxes">
              <input type ="checkbox"
              name="Wine"
              defaultChecked={this.onCheckboxChange}
              onChange = {this.handleCheckboxChange} 
              key = "Wine"
              id = "wineCheck"/>
              {OPTIONS[0]}
               <input type ="checkbox"
              name="Beer"
              defaultChecked={this.onCheckboxChange}
              onChange = {this.handleCheckboxChange} 
              key = "Beer"
              id = "beerCheck"/> 
              {OPTIONS[1]}
               <input type ="checkbox"
              name="Spirits"
              defaultChecked={this.onCheckboxChange}
              onChange = {this.handleCheckboxChange}
              key="Spirits" 
              id="spiritsCheck"/>
              {OPTIONS[2]}
            </div>
            <div className = "fileUploader">
              <input type="file" name="file" id="dataFile"/>
            </div>
            <div className = "submitButton" value="Submit">
              <button type="button" class="btn btn-success btn-block"> 
              Submit</button>
            </div>
        </form>
       </header>
    </div>
  );
  }
}
export default App;
