//imports relevant files
import React from 'react';
import ReactDOM from 'react-dom';
import {createStore} from 'redux';
import {connect} from 'react-redux';
import {craftSlide} from '../actions/changeData';

//creates the slider file
class craftSlider extends React.Component{

  //starts the states and creates the constant of start, and end which can be changed
  constructor(props){
    super(props);
   //this.handleNumber = this.handleNumber.bind(this);

}
state = {
  //the amount of slots used in the slider
  slots: 10,
  start:0,
  end: 5,
  labelMode: "mid",
}
  //uses a dragover event
  onDragOver = (e) =>{ 
    e.preventDefault();
  }
  // Implemented once the slider is clicked and just starting
  onDragStart = (e) =>{
    let slider = e.target.dataset.slider; 
    this.sliderType = slider;
  }
  //dragging event
  onDrag = (e) => {
  }
  //what happens once this Slider is dropped
  onDrop = (e,target) =>{
    // sets variables to previous variables
    let source = this.sliderType;
    let slot = Number(e.target.dataset.slot);
      //checks if slot is not a number, and if so it is returned
      if(isNaN(slot)) return;
      //sets the state to where the slider is currently
      this.setState({
        end:slot
      },()=>{
        console.log(this.state);
        })
      //this.props.handleNumber(this.state.end);

      // sets the sliderType to null
      this.sliderType = null;
  }
  // Creates the slider edge in which is used to input the values
  MaxSlider=()=>{
    //returns the html that is relevant in order to make a slider
  return(
    <div data-slider = "max"
    onDragStart={this.onDragStart}
    onTouchStart={this.onDragStart}
    onDrag={this.onDrag}
    draggable className = "slider-thumb slider-thumb-max"></div>
    );
}
  //runs the render in order to implement the application
  render(){
    //creates the variables that will be relevant later in the document
    let scale = [];
    let slider = [];
    let currentScale = [];
    let maxThumb = null;

    //goes through each of the slots and prints off the labels on the top of the line
    for(let i =0; i<=this.state.slots;i++){
          let label = "";
          //makes it so that only the number 0 5 and 10 are shown to the user
          if(i == 0 || i == 5 || i == 10){
            label = i;
          }
          // sets each of the keys relevant to the number in the for loop
          scale.push(
            <div 
            key={i} 
            className="slot-scale">
              {label}
              </div>
            );
            // does the same as above, but only once instead as it is the value the slider is currently on
            let currentLabel = "";
            //checks that this value is shown as where the slider is currently on
          if(i === this.state.end){
            currentLabel = i;
          }
          // checks that the only current Scale value is filled
            currentScale.push(<div 
              key={i} 
              className="slot-scale">
              {currentLabel}
              </div>
            );
            // checks if this state is equal to the end and then makes the variable mac Thumb equal to max Slider
            if(i === this.state.end){
              maxThumb = <this.MaxSlider />
            } else{
              //otherwise the maxThumb is bull
              maxThumb = null;
            }
            // creates a new variable of lineClass
            let lineClass = "line";

            //checks if the current number is less than the maximum and creates the line
            if(i<this.state.end){
              lineClass += " line-selected";
            }
            //implements the color and the obviousness of
            slider.push(
                <div
                data-slot={i}
                onDragOver={this.onDragOver}
                onTouchMove = {this.onDragOver}
                onTouchEnd = {this.onDrop}
                onDrop = {this.onDrop}
                key={i}
                className="slot">
                <div data-slot={i} className={lineClass}/>
                <span className="scale-mark"></span>
                {maxThumb}
                </div>
              );
          }
    return(

      <div align="center">
        <div className="topSlider">
         <div className = "slider-container">
           <div className = "slider-scale">
              {scale}
           </div>
           <div className="slider">
              {slider}
            </div>
            <div className="slider-selected-scale">
             {currentScale}
            </div>
            <div className = "slider-container">
              <div className = "slider-scale">
                {scale}
              </div>
              <div className="slider">
                {slider}
              </div>
              <div className = "slider-selected-scale">
                {currentScale}
              </div>
            </ div>
          </div>
        </div>
      </div>
    );
  }
}
function mapStateToProps(state){
  return{
    number: state
  }
}

export default craftSlider;
