//importing all relevant files to this file.
import React,{Component} from 'react';
import ReactDOM from 'react-dom';
import Slider from './Slider'
import Checkbox from './checkBox';
import axios from 'axios';

//importing css to this file
import './App.css';

// creating a Constant for the options that the users can click on
const OPTIONS = ["Wine", "Beer", "Spirits"];

const inputParsers = {
	uppercase(input){
		return input.toUpperCase();
	},
	number(input){
		return parseFloat(input);
	},
};



//overall App class which takes all
class App extends React.Component {
  //constructor for the App class in which a super with the returned values passed through
  constructor(props){
    super(props);
    this.handleSubmit = this.handleSubmit.bind(this);
    //sets the checkbox values to false so it can be presented so to people.
    this.state={
    	ProductData: [],
      checkboxes: OPTIONS.reduce(
          (options, option) => ({
            ...options,
            [option]:false
          }),
          {}
        ),
      	barname: '',
  		craftSlide: 0,
  		complexity: 0,
      	wineCheck: false,
      	beerCheck: false,
      	spiritsCheck: false,
      	location: '',
    };
  }


  // is a super function in order to select all values either selecting or de-selecting
  handleChange(event){
  	const { name } = event.target;
  	this.setState({[name]: [name].value})
  	alert(this.state.event.target);
  }

  // allows for a passing through in order to select all checkboxes as true  

  // allows for the changing between true and false for individual checkboxes
  handleCheckboxChange = changeEvent => {
    //ginds the specific checkbox to be checked off
    const { name } = changeEvent.target;
    // changes the state dependant on whether it was previously true or false
    this.setState(prevState => ({
      checkboxes: {
        ...prevState.checkboxes,
        [name]: !prevState.checkboxes[name]
      }
    }));
  };
    //allows for event and changing of the checkbox
    handleSubmit(event){
   	event.preventDefault();
   	const form = event.target;
   	const data = new FormData(form);
   	for(let name of data.keys()){
   		const input = form.elements[name];
   		const parserName = input.dataset.parse;
   		if(parserName){
   			const parser = inputParsers[parserName];
   			const parsedValue = parser(data.get(name));
   			data.set(name, parsedValue);
   		}
   	}
   	fetch('http://localhost:5000/api/BarController',{
   		method: 'POST',
   		headers:{
   			'Accept': 'application/json',
   			'Content-Type': 'application/json',
   		},
  		body: JSON.stringify({
  			barname: this.state.barname,
  			craftSlide: this.state.craftSlide,
  			complexity: this.state.complexity,
  			winecheck: this.state.wineCheck,
  			beercheck: this.state.beerCheck,
  			spiritscheck: this.state.spiritscheck,
  			location: this.state.location,
  		})
  	})
  	fetch('https://localhost:5001/api/BarController',{
   		method: 'POST',
   		headers:{
   			'Accept': 'application/json',
   			'Content-Type': 'application/json',
   		},
  		body: JSON.stringify({
  			barname: this.state.barname,
  			craftSlide: this.state.craftSlide,
  			complexity: this.state.complexity,
  			winecheck: this.state.wineCheck,
  			beercheck: this.state.beerCheck,
  			spiritscheck: this.state.spiritscheck,
  			location: this.state.location,
  		})
  	})
  };

  // allows for the creation within this class whilst using the imported files
  createCheckbox = option => (
    <Checkbox
      label={option}
      isSelected={this.state.checkboxes[option]}
      onCheckboxChange={this.handleCheckboxChange}
      key={option}
    />
  );
  // creates all the checkboxes based on the constants that are the options
  createCheckboxes = () => OPTIONS.map(this.createCheckbox);

  //calls the render function
  render(){
  	const beerSlider = <Slider name="craftSlide" onChange={this.handleChange} value={this.state.craftSlide} />;
  	const complexity = <Slider name="complexity" onChange={this.handleChange} value={this.state.complexity}/>; 
  	alert(beerSlider.end)

    //this is the return function that returns the html that helps create the app
    return (
      //a div class in order to have the specific section in question
      <div className="App">
        <header>
         <form onSubmit = {this.handleSubmit}>
           <label>
             <h2>What is the Barname: <br/></h2>
              <input type = "text" name ="barname" onChange={this.handleChange.bind(this)} value={this.state.barname} />
            </label>
            <h2> Craft Beer </h2>
            {beerSlider}
            <h2> Wine List Complexity</h2>
            {complexity}
            <div className = "checkboxes">
              <input type ="checkbox"
              defaultChecked={this.onCheckboxChange}
              onChange = {this.handleCheckboxChange} 
              key = "Wine"
              name = "wineCheck"/>
              {OPTIONS[0]}
               <input type ="checkbox"
              name="beerCheck"
              defaultChecked={this.onCheckboxChange}
              onChange = {this.handleCheckboxChange} 
              key = "Beer" /> 
              {OPTIONS[1]}
               <input type ="checkbox"
              name="spiritsCheck"
              defaultChecked={this.onCheckboxChange}
              onChange = {this.handleCheckboxChange}
              key="Spirits" />
              {OPTIONS[2]}
            </div>
            <div className = "fileUploader">
              <input type="file" name="file" id="dataFile" onChange={this.handleChange} />
            </div>
             <h2>Where is the Bar Located: <br/></h2>
              <input type = "text" name ="location" onChange={this.handleChange.bind(this)} value={this.state.location} /><br/>
            <input type="submit" value="Submit"/>
        </form>
       </header>
    </div>
  );
  }
}


export default App;
