import {CRAFT_SLIDER, COMP_SLIDER} from './types';

export const craftSlider = (value) => dispatch =>{
    dispatch({    
            type:'CRAFT_SLIDER',
            payload: value
        }
    )
}
export const compSlider = (value) => dispatch => {
    dispatch({
        type: 'COMP_SLIDER',
        payload: value
    });
}
